function getAllElementsForFormId(formName, rootElement){		
	var sql = "SELECT _URI, PERSIST_AS_TABLE_NAME, ELEMENT_TYPE, ELEMENT_NAME, PARENT_URI_FORM_DATA_MODEL, PERSIST_AS_COLUMN_NAME FROM _form_data_model WHERE URI_SUBMISSION_DATA_MODEL = "
		+ "(SELECT URI_SUBMISSION_DATA_MODEL FROM _form_info_submission_association WHERE SUBMISSION_FORM_ID = '" + formName + "')";

	var connection;
		try{
			connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
			var rs;
			var stmt;

			try{
				stmt = connection.createStatement();
				rs = stmt.executeQuery(sql);

		    	if(rs.next()) {
		        	var uri = rs.getString("_URI");
		    		var tableName = rs.getString("PERSIST_AS_TABLE_NAME");
		       		var elementType = rs.getString("ELEMENT_TYPE");
		        	var elementName = rs.getString("ELEMENT_NAME");
		        	var parentURI = rs.getString("PARENT_URI_FORM_DATA_MODEL");
		        	var columnName = rs.getString("PERSIST_AS_COLUMN_NAME");

		        	handleGroup(uri, elementType, elementName, tableName, columnName, parentURI, rootElement, connection);
		    	}
			}
			catch(e){
			}	
			finally{
				if(rs != null)
					rs.close();
				if(stmt != null)
					stmt.close();
			}	    
		} 
		catch (e) {}
		finally {
			if (connection) { 
				connection.close();
		}
	}
}

