// one function per template is recommended. i.e.) create a new code template for each new function.
function handleElement(uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection){
        if(elementType.equalsIgnoreCase("REPEAT")){
//        	System.out.println("-------------------------------------------- REPEAT found");
        }
        else if(elementType.equalsIgnoreCase("GROUP")){
//        	System.out.println("-------------------------------------------- GROUP found with name " + elementName);
        	//log("group name=" + elementName);
        	//groupIndentLevel++;

        	handleGroup(uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);

        	//groupIndentLevel--;
//        	System.out.println("-------------------------------------------- end GROUP " + elementName);
        	//log("group name=" + elementName);
        }
        else if(elementType.equalsIgnoreCase("GEOPOINT")){
//        	System.out.println("-------------------------------------------- GEOPOINT found");
        }
        else if(elementType.equalsIgnoreCase("PHANTOM")){
//        	System.out.println("-------------------------------------------- PHANTOM table found: " + tableName);
        	handlePhantom(uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }		        
        else if(elementType.equalsIgnoreCase("SELECTN")){
//        	System.out.println("-------------------------------------------- SELECTN found, table: " + tableName);
        	handleSelectNData(elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
        else if(elementType.equalsIgnoreCase("SELECT1")){
//        	System.out.println("-------------------------------------------- SELECT1 found");
        	handleSelectNData(elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
        else if(elementType.equalsIgnoreCase("BINARY")){
//        	System.out.println("-------------------------------------------- BINARY found");
        }
        //SUPPORTING TABLES
        else if(elementType.equalsIgnoreCase("BINARY_CONTENT_REF_BLOB")){
//        	System.out.println("-------------------------------------------- BINARY found");
        }
        else if(elementType.equalsIgnoreCase("REF_BLOB")){
//        	System.out.println("-------------------------------------------- BINARY found");
        }
        else{
			//logger.info("Type: " + elementType);
        	selectData(elementType, elementName, tableName, columnName, parentElement, connection);
        }
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function handleGroup(elementUri, elementType, elementName, tableName, columnName, parentURI, parentElement, conn) {
	var sql = "SELECT _URI, PERSIST_AS_TABLE_NAME, PERSIST_AS_COLUMN_NAME, ELEMENT_TYPE, PARENT_URI_FORM_DATA_MODEL, ELEMENT_NAME "
	+ "FROM _form_data_model WHERE PARENT_URI_FORM_DATA_MODEL = '" + elementUri + "'";

	var stmt;
	var rs;
	try{
		stmt = conn.createStatement();
		rs = stmt.executeQuery(sql);

		var group = <group name={elementName} tableName={tableName} type="group" />;
		var children = <></>;
		group.children = children;

		while(rs.next()) {
			var _uri = rs.getString("_URI");
		    var _tableName = rs.getString("PERSIST_AS_TABLE_NAME");
		    var _elementType = rs.getString("ELEMENT_TYPE");
		    var _parentURI = rs.getString("PARENT_URI_FORM_DATA_MODEL");
		    var _elementName = rs.getString("ELEMENT_NAME");
		    var _columnName = rs.getString("PERSIST_AS_COLUMN_NAME");

		    handleElement(_uri, _elementType, _elementName, _tableName, _columnName, _parentURI, group, conn);

			//logger.info("Found element: " + _elementName + " | ParentXML: " + parentElement.toXMLString());
		}  
		parentElement.children += group;
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

