/**
	Create XML from JS Array containing Information about odk extraform table structure
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function createTableDummy(CORE_TABLE_NAME, uuid, primaryKey, listOfObjects){
	var table = <table key={uuid} name={CORE_TABLE_NAME} primaryKey={primaryKey} />;

	/* CREATE XML FROM JS ARRAY */
	if(listOfObjects.length > 0){
		var columns = <></>;

		listOfObjects.forEach(function(entry) {
			var name = entry['name'].toString();
			var type = entry['type'].toString();
			var size = entry['size'].toString();
			var allow_null = entry['allow_null'].toString();
			var default_value = entry['default_value'];

			var column = <columns name={name} type={type} size={size} allowNull={allow_null}></columns>;

			if(default_value){
				column.@default_value = default_value;
			}
			columns += column;
		});
		table.columns = columns;
	}
	return table;
}

/**
	Extracts the Table Structure from a given ExtraForm Core Table and returns a JS Array with the info
	about each of its columns
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function getCoreTableStructure(CORE_TABLE_NAME){
	var listOfObjects = [];
	var connection;
	try {
		connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
		var metaData = connection.getMetaData();	
		var result;
		try { 
			result = metaData.getColumns(null, null, CORE_TABLE_NAME, null);
			var counter = 1;
			while (result.next()) {		
				var columnName = result.getString("COLUMN_NAME");
				var defaultValue = result.getString("COLUMN_DEF");
				var dataType = result.getInt("DATA_TYPE");
				var typeName = result.getString("TYPE_NAME");
				var nullable = result.getInt("NULLABLE");
				var columnSize = result.getInt("COLUMN_SIZE");

				var ColumnDummy = {
					'name' : columnName, 
					'type' : typeName,
					'default_value' : defaultValue,
					'size' : columnSize,
					'allow_null' : (nullable == java.sql.ResultSetMetaData.columnNoNulls)?"false":"true"
				}
				listOfObjects.push(ColumnDummy);
				counter++;
			}
		}
		finally {
			if(result){
				result.close();
			}
		}
	} finally {
		if (connection) { 
			connection.close();
		}
	}
	return listOfObjects;
}

