function getCoreTableFromFormName(extraFormName){		
	var tableName;
	var sql = "SELECT _CREATION_DATE, SUBMISSION_FORM_ID, URI_SUBMISSION_DATA_MODEL FROM _form_info_submission_association";

	var connection;
	try {
		connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));

		var stmt = connection.createStatement(); 
		var rs = stmt.executeQuery(sql);

		while (rs.next()) {
			var c = rs.getString("_CREATION_DATE");
			var form = rs.getString("SUBMISSION_FORM_ID");
			var uri = rs.getString("URI_SUBMISSION_DATA_MODEL");

			if(form.equalsIgnoreCase(extraFormName)){		        		
		    	tableName = processForm(uri);
		 	}
		}		    
	} catch (e) {
	}
	finally{
		if(connection){
			connection.close();
		}
	}
	return tableName;
}

/**
	Extracts ExtraForm Data from a given ExtraForm Table and returns a JS-Array containing objects
	(JS-Array) for each data entry, describing its colum entries
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function getExtraFormData(tableName, rowsToFetch){
	var listOfObjects = [];
	var tableNameResult = null; 
	var sql = "SELECT * FROM " + tableName + " WHERE PROCESSED_BY_MIRTH = 0 LIMIT " + rowsToFetch;
	var connection;
	try{
		connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
		var rs;
		var stmt;
		try{
			stmt = connection.createStatement();
			rs = stmt.executeQuery(sql);
			var rsmd = rs.getMetaData(); //ResultSetMetaData

			while(rs.next()){ //ResultSet should contain at most rowsToFetch entries due to the LIMIT rowsToFetch in SELECT above
				var listOfColumns = [];
				for(var counter = 1; counter <= rsmd.getColumnCount(); counter++ ){
					var columnName = rsmd.getColumnName(counter);
					var value = rs.getString(columnName);
					var columnTypeName = rsmd.getColumnTypeName(counter);

					var columnEntry = {
						'columnName' : columnName, 
						'value' : value,
						'columnTypeName' : columnTypeName
					}
					listOfColumns.push(columnEntry);
				}
				listOfObjects.push(listOfColumns);
		    }
		}
		catch(e){
		}
		finally{
			if(rs != null)
				rs.close();
			if(stmt != null)
				stmt.close();
		}
	}
	catch(e){
	}
	finally{
		if(connection != null)
			connection.close();
	}
	return listOfObjects;
}

