function getPrimaryKeyList(CORE_TABLE_NAME) {
	var connection;
	var primaryKeyList = [];
	try {
		connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
		var metaData = connection.getMetaData();	

		var result;
		try { 
			var result = metaData.getPrimaryKeys(null, null, CORE_TABLE_NAME);
			var counter = 0;
			while (result.next()) {		
				var primaryKey = result.getString("COLUMN_NAME");
				primaryKeyList.push(primaryKey);
				counter++;
			}
		}
		finally {
			if(result){
				result.close();
			}
	}
	} finally {
		if (connection) { 
			connection.close();
		}
	}
	return primaryKeyList;
}

/**
	Helper function to extract the Core Table Name from a given form URI in the ODK database
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function processForm(uri){
	var tableNameResult = null; 
	var sql = "SELECT _URI, ELEMENT_TYPE, PERSIST_AS_TABLE_NAME, ELEMENT_NAME, PERSIST_AS_COLUMN_NAME, PARENT_URI_FORM_DATA_MODEL FROM _form_data_model WHERE URI_SUBMISSION_DATA_MODEL = ?";

	var connection;
	try{
			connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
			var ps = connection.prepareStatement(sql);
			ps.setString(1, uri);

			var rs;
			try{
				var rs = ps.executeQuery();

			    if (rs.next()) {
			    	var elementType = rs.getString("ELEMENT_TYPE");
			    	var columnName = rs.getString("PERSIST_AS_COLUMN_NAME");
			    	var element_uri = rs.getString("_URI");
			    	var parent_uri = rs.getString("PARENT_URI_FORM_DATA_MODEL");
			    	var tableName = rs.getString("PERSIST_AS_TABLE_NAME");
			    	var elementName = rs.getString("ELEMENT_NAME");

			    	// Check for starting group 	
			    	if(parent_uri.equals(uri) && elementType.equalsIgnoreCase("GROUP") && (columnName == null)){
			    		tableNameResult = tableName;
			    	}
			    }
			}
			catch(e){
			}
			finally{
				if(rs != null)
					rs.close();
			}
		}
		catch(e){
		}
		finally{
			if(connection != null)
				connection.close();
		}
		return tableNameResult;
	}

function markRecordAsProcessedMigration(rawData, status) {
  var originalXml = new XML(rawData);
  // retrieve the table to update
  var tableName = originalXml["@table-name"].toString();
  var id = originalXml["id"].toString();

 var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("HRS_DATABASE_URL"), globalMap.get("HRS_DATABASE_USER"), globalMap.get("HRS_DATABASE_PASSWORD"));
  var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE id = '" + id + "'");
  dbConn.close();
}

function markRecordAsProcessed(rawData, status) {
  var originalXml = new XML(rawData);
  // retrieve the table to update
  var tableName = originalXml["@table-name"].toString();
  var uri = originalXml["_uri"].toString();


if (tableName == globalMap.get("VISIT_TABLE") ) {
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));
    var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE OPENHDS_VISIT_ID = '" + originalXml["openhds_visit_id"].toString() + "'");
    dbConn.close();
} else if (tableName !== "OMGIM") {
 var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));
  var result = dbConn.executeUpdate("UPDATE " + tableName + " SET processed_by_mirth = " + status + " WHERE _URI = '" + uri + "'");
  dbConn.close();
  } 
}

