function resetProcessedErrored() {

var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));

dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_VISIT_ID IN (select distinct OPENHDS_VISIT_ID from PREGNANCY_OBSERVATION_CORE where PROCESSED_BY_MIRTH =0)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_VISIT_ID IN (select distinct OPENHDS_VISIT_ID from PREGNANCY_OUTCOME_CORE where PROCESSED_BY_MIRTH =0)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_VISIT_ID IN (select distinct OPENHDS_VISIT_ID from IN_MIGRATION_CORE where PROCESSED_BY_MIRTH =0)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_VISIT_ID IN (select distinct OPENHDS_VISIT_ID from DEATH_REGISTRATION_CORE where PROCESSED_BY_MIRTH =0)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_VISIT_ID IN (select distinct OPENHDS_VISIT_ID from OUT_MIGRATION_REGISTRATION_CORE where PROCESSED_BY_MIRTH =0)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=0 where OPENHDS_LOCATION_ID IN (select OPENHDS_LOCATION_ID from LOCATION_REGISTRATION_CORE where PROCESSED_BY_MIRTH =0)");  
dbConn.close();  

}

/**
	Transforms a ResultSet to a List of XML forms representing as strings
*/
function resultsToForms(results, xmlElement) {
  var forms = new java.util.ArrayList();
  while(results.next()) {
    var xml = resultSetToXml(results, xmlElement)
    forms.add(xml.toString());
  }

  return forms;
}

/**
	Converts a java.sql.ResultSet to an XML representation. The XML will have elements named after the
	column names, and the values of those XML elements will be the column values
*/
// rs - the ResultSet instance
// xmlElement - a string containing the document level xml element to use for the XML document
function resultSetToXml(rs, xmlElement) {
  var rsmd = rs.getMetaData();
  var result = new XML(xmlElement);

  for (var i = 1; i <= rsmd.getColumnCount(); i++)  {
	var value = rs.getString(i);

    if (value === "null") {
      value = null;
    }

    result[rsmd.getColumnName(i).toLowerCase()] = value;
  }

  return result;
}

function addBaseline(visitId, dbConn) {
  var results = dbConn.executeCachedQuery("SELECT _URI, INDIVIDUAL_INFO_DATE_OF_BIRTH, DATE_OF_MIGRATION, INDIVIDUAL_INFO_FIRST_NAME, INDIVIDUAL_INFO_MOTHER_ID, INDIVIDUAL_INFO_INDIVIDUAL_ID, OPENHDS_VISIT_ID, INDIVIDUAL_INFO_GENDER, OPENHDS_LOCATION_ID, OPENHDS_FIELD_WORKER_ID, INDIVIDUAL_INFO_LAST_NAME, INDIVIDUAL_INFO_PARTIAL_DATE, INDIVIDUAL_INFO_FATHER_ID, ifnull(INDIVIDUAL_INFO_MIDDLE_NAME, '') INDIVIDUAL_INFO_MIDDLE_NAME FROM " + globalMap.get("BASELINE_TABLE") + " WHERE OPENHDS_VISIT_ID = '" + visitId + "' AND openhds_migration_type = 'BASELINE' AND processed_by_mirth=0 order by INDIVIDUAL_INFO_DATE_OF_BIRTH ASC");
  var baselineForms = resultsToForms(results, "<baseline event='baseline' table-name='" + globalMap.get("BASELINE_TABLE") + "'/>");
  return baselineForms;
}

/**
	changeHoH(visitId, dbConn)

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function changeHoH(visitId, dbConn) {
	var changeHoHForm = new java.util.ArrayList();
	var results = dbConn.executeCachedQuery(
			"SELECT _URI, OPENHDS_VISIT_ID, OPENHDS_FIELD_WORKER_ID, OPENHDS_NEW_HOH_ID, OPENHDS_INDIVIDUAL_ID, "
			+ "OPENHDS_HOUSEHOLD_ID, DATE " 
			+ "FROM " + globalMap.get("CHANGE_HOH_TABLE") 
			+ " WHERE OPENHDS_VISIT_ID = '" + visitId + "' AND (processed_by_mirth is null OR processed_by_mirth = 0)");

	while(results.next()) {
		var changeHoH = resultSetToXml(results, "<changeHoH event='change-hoh' table-name='" + globalMap.get("CHANGE_HOH_TABLE") + "' />");
		var relations = dbConn.executeCachedQuery(
				"SELECT MEMBER_NAME, RELATIONSHIP_TO_GROUP_HEAD, EXT_ID, SOCIAL_GROUP_ID " 
				+ "FROM " + globalMap.get("CHANGE_HOH_RELATIONS") 
				+ " WHERE _PARENT_AURI = '" + changeHoH["_uri"].toString() + "'");
		while(relations.next()){
			var relation = resultSetToXml(relations, "<relation />");
			changeHoH.appendChild(relation);
		}
		changeHoHForm.add(changeHoH.toString());
	}
	return changeHoHForm;
}

