/**
	Retrieve Pregnancy Outcome Forms
*/
// this is the most complicated function for retrieving forms
// the following assumptions were made when writing this function:
// In Migrations will be processed before pregnancy outcomes. 
// This ensures if a women has in migrated, the individual will be created in the system before the pregnancy outcome
// Also, it's possible this pregnancy outcome depends 
function addPregnancyOutcomes(visitId, dbConn) {
  var forms = new java.util.ArrayList();
  var results = dbConn.executeCachedQuery("SELECT _URI, OPENHDS_VISIT_ID, OPENHDS_MOTHER_ID, PARTIAL_DATE, OPENHDS_FIELD_WORKER_ID, RECORDED_DATE, OPENHDS_FATHER_ID FROM " + globalMap.get("PREGNANCY_OUTCOME_TABLE") + " WHERE OPENHDS_VISIT_ID = '" + visitId + "' AND processed_by_mirth =0");
  while(results.next()) {
    var pregOut = resultSetToXml(results, "<pregnancyoutcome event='pregnancyoutcome' table-name='" + globalMap.get("PREGNANCY_OUTCOME_TABLE") + "' />");
    // are there any outcomes for this pregnancy outcome?
    var outcomes = dbConn.executeCachedQuery("SELECT OUTCOME_TYPE, FIRST_NAME, SOCIAL_GROUP_ID, GENDER, RELATIONSHIP_TO_GROUP_HEAD, CHILD_ID, LAST_NAME, MIDDLE_NAME FROM " + globalMap.get("OUTCOMES_TABLE") + " WHERE _PARENT_AURI = '" + pregOut["_uri"].toString() + "'");
    while(outcomes.next()) {
      var outcome = resultSetToXml(outcomes, "<outcome />");
      pregOut.appendChild(outcome);
    }

    forms.add(pregOut.toString());
  }

  return forms;
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function addDataElement(parentElement, columnName, type, value) {
	//logger.info("Adding " + columnName + " to " + type);
	var data = <data type={type} columnName={columnName} value={value} />;
	parentElement.children += data;
}

function getDataFromElement(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection){
	var sql;

    //if(parentURI.indexOf("phantom") > -1 && !tableName.equalsIgnoreCase(coreTableName)){
	if(!tableName.equalsIgnoreCase(coreTableName)){
    	sql = "SELECT " + columnName + " FROM " + tableName + " WHERE _TOP_LEVEL_AURI = '" + submissionUri + "'" ;
    }
    else{
    	sql = "SELECT " + columnName + " FROM " + tableName + " WHERE _URI = '" + submissionUri + "'";
    }
	var stmt;
	var rs;
	try{
		stmt = connection.createStatement();
		rs = stmt.executeQuery(sql);

		while(rs.next()) {
			var dataEntry = rs.getString(columnName);
		    var element = <data columnTypeName={elementType} value={dataEntry} columnName={columnName} type={elementType} />;
			parentElement.children += element;
		}  
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

/**
	SEND::getDataFromGroup
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function getDataFromGroup(submissionUri, coreTableName, elementUri, elementType, elementName, tableName, columnName, parentURI, parentElement, conn) {
	var sql = "SELECT _URI, PERSIST_AS_TABLE_NAME, PERSIST_AS_COLUMN_NAME, ELEMENT_TYPE, PARENT_URI_FORM_DATA_MODEL, ELEMENT_NAME "
				+ "FROM _form_data_model WHERE PARENT_URI_FORM_DATA_MODEL = '" + elementUri + "'";
	var stmt;
	var rs;
	try{
		stmt = conn.createStatement();
		rs = stmt.executeQuery(sql);

		var group = <group name={elementName} tableName={tableName} type="group" />;
		var children = <></>;
		group.children = children;

		while(rs.next()) {
			var _uri = rs.getString("_URI");
		    var _tableName = rs.getString("PERSIST_AS_TABLE_NAME");
		    var _elementType = rs.getString("ELEMENT_TYPE");
		    var _parentURI = rs.getString("PARENT_URI_FORM_DATA_MODEL");
		    var _elementName = rs.getString("ELEMENT_NAME");
		    var _columnName = rs.getString("PERSIST_AS_COLUMN_NAME");

		    handleElementsGetData(submissionUri, coreTableName, _uri, _elementType, _elementName, _tableName, _columnName, _parentURI, group, conn);
			//logger.info("Found element: " + _elementName + " | groupXML: " + group.toXMLString());
		}  
		parentElement.children += group;
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

function getDataFromPhantom(submissionUri, coreTableName, elementUri, elementType, elementName, tableName, columnName, parentURI, parentElement, conn) {

	//logger.info("Get elements for elementURI: " + elementUri);

	var sql = "SELECT _URI, PERSIST_AS_TABLE_NAME, ELEMENT_TYPE, ELEMENT_NAME, PARENT_URI_FORM_DATA_MODEL, PERSIST_AS_COLUMN_NAME " +
		"FROM _form_data_model WHERE PARENT_URI_FORM_DATA_MODEL = '" + elementUri + "'";
	var stmt;
	var rs;
	try{
		stmt = conn.createStatement();
		rs = stmt.executeQuery(sql);
		while(rs.next()) {
			var _uri = rs.getString("_URI");
		    var _tableName = rs.getString("PERSIST_AS_TABLE_NAME");
		    var _elementType = rs.getString("ELEMENT_TYPE");
		    var _parentURI = rs.getString("PARENT_URI_FORM_DATA_MODEL");
		    var _elementName = rs.getString("ELEMENT_NAME");
		    var _columnName = rs.getString("PERSIST_AS_COLUMN_NAME");  

		    handleElementsGetData(submissionUri, coreTableName, _uri, _elementType, _elementName, _tableName, _columnName, _parentURI, parentElement, conn);
			//logger.info("Found element: " + _elementName + " | groupXML: " + group.toXMLString());
			//logger.info("Phantom element of type: " + _elementType);
		}  
		parentElement.children += group;
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function getDataFromSelectN(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection) {
	var sql = "SELECT " + columnName + " FROM " + tableName + " WHERE _PARENT_AURI = '" + parentURI + "'";
	var stmt;
	var rs;
	try{
		stmt = connection.createStatement();
		rs = stmt.executeQuery(sql);

		while(rs.next()) {
			var dataEntry = rs.getString("value");
		    var element = <data columnTypeName={elementType} value={dataEntry} columnName={tableName} type={elementType} />; // Take tableName instead of columnName, since this is null
			parentElement.children += element;
		}  
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

function getSubmissionCoreData(submissionUri, coreTableName, rootElement, connection) {
	var sql = "SELECT _URI, _CREATOR_URI_USER, _CREATION_DATE, _LAST_UPDATE_URI_USER, _LAST_UPDATE_DATE, _MODEL_VERSION, _UI_VERSION, _IS_COMPLETE, _SUBMISSION_DATE, _MARKED_AS_COMPLETE_DATE "
				+ "FROM " + coreTableName + " WHERE _URI = '" + submissionUri + "'";
	var stmt;
	var rs;
	try{
		stmt = connection.createStatement();
		rs = stmt.executeQuery(sql);

		if(rs.next()) {
			var _uri = rs.getString("_URI");
		    var _creatorUriUser = rs.getString("_CREATOR_URI_USER");
		    var _creationDate = rs.getString("_CREATION_DATE");
		    var _lastUpdateUriUser = rs.getString("_LAST_UPDATE_URI_USER");
		    var _lastUpdateDate = rs.getString("_LAST_UPDATE_DATE");
		    var _modelVersion = rs.getString("_MODEL_VERSION");
		    var _uiVersion = rs.getString("_UI_VERSION");
		    var _isComplete = rs.getString("_IS_COMPLETE");
		    var _submissionDate = rs.getString("_SUBMISSION_DATE");
		    var _markedAsCompleteDate = rs.getString("_MARKED_AS_COMPLETE_DATE");

			addDataElement(rootElement, "_URI", "VARCHAR", _uri);
			addDataElement(rootElement, "_CREATOR_URI_USER", "VARCHAR", _creatorUriUser);
			addDataElement(rootElement, "_CREATION_DATE", "DATETIME", _creationDate);
			addDataElement(rootElement, "_LAST_UPDATE_URI_USER", "VARCHAR", _lastUpdateUriUser);
			addDataElement(rootElement, "_LAST_UPDATE_DATE", "DATETIME", _lastUpdateDate);
			addDataElement(rootElement, "_MODEL_VERSION", "INT", _modelVersion);
			addDataElement(rootElement, "_UI_VERSION", "INT", _uiVersion);
			addDataElement(rootElement, "_IS_COMPLETE", "CHAR", _isComplete);
			addDataElement(rootElement, "_SUBMISSION_DATE", "DATETIME", _submissionDate);
			addDataElement(rootElement, "_MARKED_AS_COMPLETE_DATE", "DATETIME", _markedAsCompleteDate);
		}  
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function getSubmissionList(tableName, connection){
	var rowsToFetch = 10;
	var sql = "SELECT _URI FROM " + tableName + " WHERE PROCESSED_BY_MIRTH = 0 LIMIT " + rowsToFetch;  //For production also add a WHERE PROCESSEDBYMIRTH = 0 !
	//var sql = "SELECT _URI FROM " + tableName + " LIMIT " + rowsToFetch;  //For production also add a WHERE PROCESSEDBYMIRTH = 0 !

	var submissionURIList = [];
	var stmt;
	var rs;

	try{
		stmt = connection.createStatement();
		rs = stmt.executeQuery(sql);
		while(rs.next()) {
			var _uri = rs.getString("_URI");
			//logger.info("Found submission with uri: " + _uri);
		    submissionURIList.push(_uri);
		}  
		return submissionURIList;
	}
	catch(e){
	}	
	finally{
		if(rs != null)
			rs.close();
		if(stmt != null)
			stmt.close();
	}
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function handleElementsGetData(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection) {
	    if(elementType.equalsIgnoreCase("REPEAT")){
        }
        else if(elementType.equalsIgnoreCase("GROUP")){
        	getDataFromGroup(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
        else if(elementType.equalsIgnoreCase("GEOPOINT")){
        }
        else if(elementType.equalsIgnoreCase("PHANTOM")){
        	getDataFromPhantom(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }		        
        else if(elementType.equalsIgnoreCase("SELECTN")){
        	getDataFromSelectN(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
        else if(elementType.equalsIgnoreCase("SELECT1")){
        	getDataFromSelectN(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
        else if(elementType.equalsIgnoreCase("BINARY")){
        }
        //SUPPORTING TABLES
        else if(elementType.equalsIgnoreCase("BINARY_CONTENT_REF_BLOB")){
        }
        else if(elementType.equalsIgnoreCase("REF_BLOB")){
        }
        else{
        	getDataFromElement(submissionUri, coreTableName, uri, elementType, elementName, tableName, columnName, parentURI, parentElement, connection);
        }
}

function readDataXml(formId, coreTable){	
	var list = new java.util.ArrayList();
	var sql = "SELECT _URI, "
		+ "PERSIST_AS_TABLE_NAME, ELEMENT_TYPE, ELEMENT_NAME, PARENT_URI_FORM_DATA_MODEL, PERSIST_AS_COLUMN_NAME "
		+ "FROM _form_data_model WHERE URI_SUBMISSION_DATA_MODEL = "
		+ "(SELECT URI_SUBMISSION_DATA_MODEL FROM _form_info_submission_association WHERE SUBMISSION_FORM_ID = '" + formId + "')";		
	var connection;
		try{
			connection = DatabaseConnectionFactory.createConnection('com.mysql.jdbc.Driver',globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"),globalMap.get("ODK_DATABASE_PASSWORD"));
			var rs;
			var stmt;
			try{
				stmt = connection.createStatement();
				rs = stmt.executeQuery(sql);

		    	while(rs.next()) {
					var _elementName = rs.getString("ELEMENT_NAME");

					if(_elementName!= null && _elementName.equalsIgnoreCase(formId)){	
				    	var _uri = rs.getString("_URI");
				    	var _tableName = rs.getString("PERSIST_AS_TABLE_NAME");
				    	var _elementType = rs.getString("ELEMENT_TYPE");
				    	var _parentURI = rs.getString("PARENT_URI_FORM_DATA_MODEL");
				    	var _columnName = rs.getString("PERSIST_AS_COLUMN_NAME");

						var submissionList = getSubmissionList(_tableName, connection);;
						for	(var index = 0; index < submissionList.length; index++) {

							var rootElement = <extraform formName={formId} coreTable={coreTable} />;
							var children = <></>;
							rootElement.children = children;

							var currentSubmissionURI = submissionList[index];
							//rootElement.@coreTable = _tableName;
							//rootElement.@uri = currentSubmissionURI;
							getSubmissionCoreData(currentSubmissionURI, _tableName, rootElement, connection);
							getDataFromGroup(currentSubmissionURI, _tableName, _uri, _elementType, _elementName, _tableName, _columnName, _parentURI, rootElement, connection);

							//filteredRootElement.@coreTable = _tableName;
							//filteredRootElement.@uri = currentSubmissionURI;

							//filteredRootElement = <extraform formName={formId} />;
							filteredRootElement = <extraform formName={formId} coreTable={coreTable} uri={currentSubmissionURI}/>;
							var new_children = <></>;
							filteredRootElement.data = new_children;

							filterDataElements(rootElement, filteredRootElement);	

							list.add(filteredRootElement);

							//logger.info("Filtered: " + filteredRootElement.toXMLString());
						}				
					}
		    	}
			}
			catch(e){
			}	
			finally{
				if(rs != null)
					rs.close();
				if(stmt != null)
					stmt.close();
			}	    
		} 
		catch (e) {}
		finally {
			if (connection) { 
				connection.close();
		}
	}
	return list;
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function filterDataElements(element, filteredElement) {
	for each (child in element.children()) {
		if(child.@type != "group"){
			var column = child.@columnName;
			var type = child.@columnTypeName;
			var value = child.@value;
			var elementType = child.@type;
			var newValue = value;
			var data = <data type={elementType} columnName={column} value={value} >{newValue}</data>;
			filteredElement.data += data;

			if(value && value.length > 0){
				//logger.info("Element has value: " + value);
			}
		}
		filterDataElements(child, filteredElement);
	}
}
