function resultsToForms(results, xmlElement) {
  var forms = new java.util.ArrayList();
  while(results.next()) {
    var xml = resultSetToXml(results, xmlElement)
    forms.add(xml.toString());
  }

  return forms;
}

function addOutMigrations(visitId, dbConn) {
  var results = dbConn.executeCachedQuery("SELECT _URI, VISIT_ID, NAME_OF_DESTINATION, FIELD_WORKER_ID, DATE_OF_MIGRATION, REASON_FOR_OUT_MIGRATION, INDIVIDUAL_ID FROM " + globalMap.get("OUT_MIGRATION_TABLE") + " WHERE VISIT_ID = '" + visitId + "' AND processed_by_mirth =0");
  var forms = resultsToForms(results, "<outmigration event='outmigration' table-name='" + globalMap.get("OUT_MIGRATION_TABLE") + "' />");
  return forms;
}

function addOutMigrationsIM(visitId, dbConn) {

var results = dbConn.executeCachedQuery("SELECT _URI, VISIT_ID, LOCATION_ID NAME_OF_DESTINATION, FIELD_WORKER_ID, DATE_SUB(DATE_OF_MIGRATION, INTERVAL 1 DAY) DATE_OF_MIGRATION, REASON as REASON_FOR_OUT_MIGRATION, INDIVIDUAL_INFO_INDIVIDUAL_ID as INDIVIDUAL_ID FROM " + globalMap.get("IN_MIGRATION_TABLE") + " WHERE VISIT_ID = '" + visitId + "' AND migration_type = 'INTERNAL_INMIGRATION' AND processed_by_mirth =0 and INDIVIDUAL_INFO_INDIVIDUAL_ID not in (select INDIVIDUAL_ID FROM " + globalMap.get("OUT_MIGRATION_TABLE")+")");  

var forms = resultsToForms(results, "<outmigration event='outmigrationIM' table-name='OMGIM' />");
  return forms;
}

// this is the most complicated function for retrieving forms
// the following assumptions were made when writing this function:
// In Migrations will be processed before pregnancy outcomes. 
// This ensures if a women has in migrated, the individual will be created in the system before the pregnancy outcome
// Also, it's possible this pregnancy outcome depends 
function addPregnancyOutcomes(visitId, dbConn) {
  var forms = new java.util.ArrayList();
  var results = dbConn.executeCachedQuery("SELECT _URI, VISIT_ID, MOTHER_ID, PARTIAL_DATE,FIELD_WORKER_ID, RECORDED_DATE, FATHER_ID FROM " + globalMap.get("PREGNANCY_OUTCOME_TABLE") + " WHERE VISIT_ID = '" + visitId + "' AND processed_by_mirth =0");
  while(results.next()) {
    var pregOut = resultSetToXml(results, "<pregnancyoutcome event='pregnancyoutcome' table-name='" + globalMap.get("PREGNANCY_OUTCOME_TABLE") + "' />");
    // are there any outcomes for this pregnancy outcome?
    var outcomes = dbConn.executeCachedQuery("SELECT OUTCOME_TYPE, FIRST_NAME, SOCIAL_GROUP_ID, GENDER, RELATIONSHIP_TO_GROUP_HEAD, CHILD_ID, LAST_NAME FROM " + globalMap.get("OUTCOMES_TABLE") + " WHERE _PARENT_AURI = '" + pregOut["_uri"].toString() + "'");
    while(outcomes.next()) {
      var outcome = resultSetToXml(outcomes, "<outcome />");
      pregOut.appendChild(outcome);
    }

    forms.add(pregOut.toString());
  }

  return forms;
}

