function resetProcessedErrored() {

var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));

dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  VISIT_ID IN (select distinct VISIT_ID from PREGNANCY_OBSERVATION_CORE where PROCESSED_BY_MIRTH =2)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  VISIT_ID IN (select distinct VISIT_ID from PREGNANCY_OUTCOME_CORE where PROCESSED_BY_MIRTH =2)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  VISIT_ID IN (select distinct VISIT_ID from IN_MIGRATION_CORE where PROCESSED_BY_MIRTH =2)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  VISIT_ID IN (select distinct VISIT_ID from DEATH_REGISTRATION_CORE where PROCESSED_BY_MIRTH =2)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  VISIT_ID IN (select distinct VISIT_ID from OUT_MIGRATION_REGISTRATION_CORE where PROCESSED_BY_MIRTH =2)");
dbConn.executeUpdate("update VISIT_REGISTRATION_CORE set PROCESSED_BY_MIRTH=false where  LOCATION_ID IN (select LOCATION_ID from LOCATION_REGISTRATION_CORE where PROCESSED_BY_MIRTH =2)");  
dbConn.close();  

}

function addBaseline(visitId, dbConn) {
  var results = dbConn.executeCachedQuery("SELECT _URI, INDIVIDUAL_INFO_DATE_OF_BIRTH, DATE_OF_MIGRATION, INDIVIDUAL_INFO_FIRST_NAME, INDIVIDUAL_INFO_MOTHER_ID, INDIVIDUAL_INFO_INDIVIDUAL_ID, VISIT_ID, INDIVIDUAL_INFO_GENDER, LOCATION_ID, FIELD_WORKER_ID, INDIVIDUAL_INFO_LAST_NAME, INDIVIDUAL_INFO_PARTIAL_DATE, INDIVIDUAL_INFO_FATHER_ID FROM " + globalMap.get("BASELINE_TABLE") + " WHERE VISIT_ID = '" + visitId + "' AND migration_type = 'BASELINE' AND processed_by_mirth=0 order by INDIVIDUAL_INFO_DATE_OF_BIRTH ASC");
  var baselineForms = resultsToForms(results, "<baseline event='baseline' table-name='" + globalMap.get("BASELINE_TABLE") + "'/>");
  return baselineForms;
}

