properties.script:
var forms = new java.util.ArrayList();
var visitIdList = new java.util.ArrayList();

var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));

var visitResult = dbConn.executeCachedQuery("SELECT DISTINCT VISIT_DATE, OPENHDS_VISIT_ID, OPENHDS_LOCATION_ID, OPENHDS_FIELD_WORKER_ID, OPENHDS_ROUND_NUMBER, REAL_VISIT FROM " + globalMap.get("VISIT_TABLE") + " WHERE processed_by_mirth=0 OR processed_by_mirth IS NULL order by OPENHDS_VISIT_ID ASC");

while(visitResult.next()) {
  var visitXml = resultSetToXml(visitResult, "<visit event='visit' table-name='" + globalMap.get("VISIT_TABLE") + "'/>");
  // does this visit depend on a newly created location ?
  // a visit will not be processed correctly if the location it refers to does not yet exist
  var location = dbConn.executeCachedQuery("SELECT _URI, OPENHDS_HIERARCHY_ID, OPENHDS_LOCATION_ID, OPENHDS_FIELD_WORKER_ID, LOCATION_TYPE, LOCATION_NAME, GEOPOINT_ACC ACCURACY, GEOPOINT_ALT ALTITUDE, GEOPOINT_LNG LONGITUDE, GEOPOINT_LAT LATITUDE FROM " + globalMap.get("LOCATION_TABLE") + " WHERE OPENHDS_LOCATION_ID = '" + visitXml['openhds_location_id'].toString() + "' AND processed_by_mirth =0");

  if (location.next()) {
    var loc = resultSetToXml(location, "<location event='location' table-name='" + globalMap.get("LOCATION_TABLE") + "'/>");
    forms.add(loc.toString());
  }

  forms.add(visitXml.toString());

  var visitId = visitXml["openhds_visit_id"].toString();

  if(!visitIdList.contains(visitId)){
	  forms.addAll(addOutMigrations(visitId, dbConn));
	  forms.addAll(addOutMigrationsIM(visitId, dbConn));
	  forms.addAll(addInternalInmigrations(visitId, dbConn));
	  forms.addAll(addExternalInmigrations(visitId, dbConn));
	  forms.addAll(addPregnancyOutcomes(visitId, dbConn));
	  forms.addAll(addPregnancyObs(visitId, dbConn));
	  forms.addAll(addDeaths(visitId, dbConn));
	  forms.addAll(addHoHDeaths(visitId, dbConn));
	  forms.addAll(changeHoH(visitId, dbConn));
	  visitIdList.add(visitId);
  }
}


dbConn.close();
// You may access this result below with $('column_name')
return forms;

