	relationsXml['membership'][i]['individual']['extId'] = msg['relation'][i]['ext_id'].toString().toUpperCase();
	relationsXml['membership'][i]['socialGroup']['extId'] = msg['relation'][i]['social_group_id'].toString().toUpperCase();
}

tmp['memberships'] = relationsXml;

