step.script:
// converts to OpenHDS pregnancy outcome XML
// basic fields
tmp['collectedBy']['extId'] = msg['openhds_field_worker_id'].toString().toUpperCase();
tmp['visit']['extId'] = msg['openhds_visit_id'].toString().toUpperCase();
tmp['outcomeDate'] = msg['recorded_date'].toString();
tmp['mother']['extId'] = msg['openhds_mother_id'].toString().toUpperCase();
tmp['father']['extId'] = msg['openhds_father_id'].toString().toUpperCase();

// outcomes
var outcomesXml = new XML("<outcomes />");
var outcomes = msg['outcome'].length();
for(var i = 0; i < outcomes; i++) {
  outcomesXml.appendChild(new XML("<outcome />"));
  if (msg['outcome'][i]['outcome_type'].toString() == "LBR") {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['outcome_type'].toString();
    outcomesXml['outcome'][i]['child']['extId'] = msg['outcome'][i]['child_id'].toString();
    outcomesXml['outcome'][i]['child']['firstName'] = msg['outcome'][i]['first_name'].toString();
    outcomesXml['outcome'][i]['child']['middleName'] = msg['outcome'][i]['middle_name'].toString();
    outcomesXml['outcome'][i]['child']['lastName'] = msg['outcome'][i]['last_name'].toString();
    outcomesXml['outcome'][i]['child']['gender'] = msg['outcome'][i]['gender'].toString();
    outcomesXml['outcome'][i]['child']['partialDate'] = msg['partial_date'].toString();
    outcomesXml['outcome'][i]['childMembership']['socialGroup']['extId'] = msg['outcome'][i]['social_group_id'].toString();
    outcomesXml['outcome'][i]['childMembership']['bIsToA'] = (parseInt(msg['outcome'][i]['relationship_to_group_head'])+ 1).toString();
  } else {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['outcome_type'].toString();
  }
}

tmp["outcomes"] = outcomesXml;

