properties.property:
var forms = new java.util.ArrayList()

var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver', globalMap.get("ODK_DATABASE_URL"), globalMap.get("ODK_DATABASE_USER"), globalMap.get("ODK_DATABASE_PASSWORD"));
var visitResult = dbConn.executeCachedQuery("SELECT _URI, VISIT_DATE, VISIT_ID, LOCATION_ID, FIELD_WORKER_ID, ROUND_NUMBER FROM " + globalMap.get("VISIT_TABLE") + " WHERE processed_by_mirth is null LIMIT 20");

while(visitResult.next()) {
  var visitXml = resultSetToXml(visitResult, "<visit event='visit' table-name='" + globalMap.get("VISIT_TABLE") + "'/>");
  // does this visit depend on a newly created location ?
  // a visit will not be processed correctly if the location it refers to does not yet exist
  var location = dbConn.executeCachedQuery("SELECT _URI, HIERARCHY_ID, LOCATION_ID, FIELD_WORKER_ID, LOCATION_TYPE, LOCATION_NAME, GEOPOINT_ACC, GEOPOINT_ALT, GEOPOINT_LNG, GEOPOINT_LAT FROM " + globalMap.get("LOCATION_TABLE") + " WHERE LOCATION_ID = '" + visitXml['location_id'].toString() + "' AND processed_by_mirth is null");

  if (location.next()) {
    var loc = resultSetToXml(location, "<location event='location' table-name='" + globalMap.get("LOCATION_TABLE") + "'/>");
    forms.add(loc.toString());
  }

  forms.add(visitXml.toString());

  var visitId = visitXml["visit_id"].toString();

  forms.addAll(addDeaths(visitId, dbConn));
  forms.addAll(addOutMigrations(visitId, dbConn));
  forms.addAll(addOutMigrationsIM(visitId, dbConn));
  forms.addAll(addPregnancyObs(visitId, dbConn));
  forms.addAll(addInternalInmigrations(visitId, dbConn));
  forms.addAll(addExternalInmigrations(visitId, dbConn));
  forms.addAll(addPregnancyOutcomes(visitId, dbConn));
}


dbConn.close();
// You may access this result below with $('column_name')
return forms;

step.script:
// converts to OpenHDS pregnancy outcome XML
// basic fields
tmp['collectedBy']['extId'] = msg['field_worker_id'].toString().toUpperCase();
tmp['visit']['extId'] = msg['visit_id'].toString().toUpperCase();
tmp['outcomeDate'] = msg['recorded_date'].toString();
tmp['mother']['extId'] = msg['mother_id'].toString().toUpperCase();
tmp['father']['extId'] = msg['father_id'].toString().toUpperCase();

// outcomes
var outcomesXml = new XML("<outcomes />");
var outcomes = msg['outcome'].length();
for(var i = 0; i < outcomes; i++) {
  outcomesXml.appendChild(new XML("<outcome />"));
  if (msg['outcome'][i]['outcome_type'].toString() == "LBR") {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['outcome_type'].toString();
    outcomesXml['outcome'][i]['child']['extId'] = msg['outcome'][i]['child_id'].toString();
    outcomesXml['outcome'][i]['child']['firstName'] = msg['outcome'][i]['first_name'].toString();
    outcomesXml['outcome'][i]['child']['lastName'] = msg['outcome'][i]['last_name'].toString();
    outcomesXml['outcome'][i]['child']['gender'] = msg['outcome'][i]['gender'].toString();
    outcomesXml['outcome'][i]['child']['partialDate'] = msg['partial_date'].toString();
    outcomesXml['outcome'][i]['childMembership']['socialGroup']['extId'] = msg['outcome'][i]['social_group_id'].toString()
    outcomesXml['outcome'][i]['childMembership']['bIsToA'] = msg['outcome'][i]['relationship_to_group_head'].toString();
  } else {
    outcomesXml['outcome'][i]['type'] = msg['outcome'][i]['outcome_type'].toString();
  }
}

tmp["outcomes"] = outcomesXml;

